import React, { useState } from 'react';
import Header from '../../components/header/Header';

function FavoriteRecipes() {
  const [favRecipes, setFavRecipes] = useState([]);
  const getFavoriteRecipe = () => {
    const recipeFavorites = localStorage.getItem('favoriteRecipes');
    const arrFavoriteRecipes = JSON.parse(recipeFavorites);
    setFavRecipes(arrFavoriteRecipes);
    console.log(favRecipes);
  };
  getFavoriteRecipe();
  return (
    <div>
      <Header
        label="Favorite Recipes"
        testid="page-title"
      />
      { favRecipes.map((mealCategory, i) => (
        <div key={ i }>
          <img
            src={ mealCategory.image }
            alt={ mealCategory.name }
          />
          <h2>{mealCategory.name}</h2>
          <h3>{mealCategory.category}</h3>
          <p>{mealCategory.nationality}</p>
          <span>Foods</span>
        </div>))}
    </div>
  );
}
export default FavoriteRecipes;

